CREATE TABLE counters (\n    table_name VARCHAR(255) PRIMARY KEY,\n    current_value INT NOT NULL\n);\n\nINSERT INTO counters (table_name, current_value) VALUES ('your_table', 0);\n\nCREATE TABLE your_table (\n    id INT,\n    data VARCHAR(255)\n);\n\nDELIMITER //\nCREATE TRIGGER before_insert_your_table\nBEFORE INSERT ON your_table\nFOR EACH ROW\nBEGIN\n    DECLARE new_id INT;\n    \n    -- Increment the counter\n    UPDATE counters SET current_value = current_value + 1 WHERE table_name = 'your_table';\n    \n    -- Get the new counter value\n    SELECT current_value INTO new_id FROM counters WHERE table_name = 'your_table';\n    \n    -- Set the new id value\n    SET NEW.id = new_id;\nEND;\n//\nDELIMITER ;\n\n-- Example insert\nINSERT INTO your_table (data) VALUES ('example data');\nINSERT INTO your_table (data) VALUES ('more data');\n\nSELECT * FROM your_table;