CREATE TABLE Event (\n    event_id INT PRIMARY KEY AUTO_INCREMENT,\n    event_name VARCHAR(255) NOT NULL,\n    description TEXT,\n    location VARCHAR(255)\n);\n\nCREATE TABLE Schedule (\n    schedule_id INT PRIMARY KEY AUTO_INCREMENT,\n    event_id INT,\n    start_time TIME,\n    end_time TIME,\n    FOREIGN KEY (event_id) REFERENCES Event(event_id) ON DELETE CASCADE\n);\n\nCREATE TABLE RecurrencePattern (\n    recurrence_id INT PRIMARY KEY AUTO_INCREMENT,\n    schedule_id INT,\n    recurrence_type ENUM('Daily', 'Weekly', 'Monthly', 'Yearly'),\n    recurrence_interval INT, -- for example, every 2 days, every 3 weeks, etc.\n    day_of_week ENUM('Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'), -- for weekly recurrence\n    day_of_month INT, -- for monthly recurrence\n    month_of_year INT, -- for yearly recurrence\n    nth_occurrence INT, -- for specific occurrences like "second Thursday"\n    FOREIGN KEY (schedule_id) REFERENCES Schedule(schedule_id) ON DELETE CASCADE\n);\n\n-- Example for storing a specific date event (one-time event)\nCREATE TABLE SpecificDate (\n    specific_date_id INT PRIMARY KEY AUTO_INCREMENT,\n    schedule_id INT,\n    event_date DATE,\n    FOREIGN KEY (schedule_id) REFERENCES Schedule(schedule_id) ON DELETE CASCADE\n);