BEGIN\n    DBMS_SCHEDULER.create_job (\n        job_name        => 'reset_sequence_job',\n        job_type        => 'PLSQL_BLOCK',\n        job_action      => 'BEGIN reset_sequence; END;',\n        start_date      => TO_TIMESTAMP_TZ('YYYY-01-01 00:00:00 +00:00', 'YYYY-MM-DD HH24:MI:SS TZR'),\n        repeat_interval => 'FREQ=YEARLY; BYDATE=0101;',\n        enabled         => TRUE\n    );\nEND;\n/