DECLARE\n  l_xml XMLTYPE := XMLTYPE('<?xml version="1.0"?>\n                           <users>\n                             <user>\n                               <name>user1</name>\n                             </user>\n                             <user>\n                               <name>user2</name>\n                             </user>\n                             <user>\n                               <name>user3</name>\n                             </user>\n                           </users>');\nBEGIN\n  FOR r IN (\n    SELECT x.name\n    FROM XMLTABLE('/users/user' \n           PASSING l_xml \n           COLUMNS name VARCHAR2(50) PATH 'name') x\n  ) \n  LOOP\n    DBMS_OUTPUT.PUT_LINE('User name: ' || r.name);\n  END LOOP;\nEND;\n/