-- Enable xp_cmdshell if it is not already enabled\nEXEC sp_configure 'show advanced options', 1;\nRECONFIGURE;\nEXEC sp_configure 'xp_cmdshell', 1;\nRECONFIGURE;\n\n-- Define file paths\nDECLARE @TemplatePath NVARCHAR(255) = N'C:\template.xls';\nDECLARE @NewFilePath NVARCHAR(255) = N'C:\newfile.xls';\n\n-- Copy the template file to a new file\nDECLARE @Cmd NVARCHAR(4000);\nSET @Cmd = N'COPY "' + @TemplatePath + N'" "' + @NewFilePath + N'"';\nEXEC xp_cmdshell @Cmd;\n\n-- Export data to the new Excel file\nINSERT INTO OPENROWSET('Microsoft.Jet.OLEDB.4.0',\n'Excel 8.0;Database=C:\newfile.xls;',\n'SELECT * FROM [SheetName$]')\nSELECT * FROM myTable;