CREATE PROCEDURE GenerateOrderSummaryReport\n    @StartDate DATE = NULL,\n    @EndDate DATE = NULL,\n    @Status VARCHAR(50) = NULL,\n    @GeneratedByUser VARCHAR(50) = NULL,\n    @MinAmount DECIMAL(18, 2) = NULL,\n    @MaxAmount DECIMAL(18, 2) = NULL\nAS\nBEGIN\n    DECLARE @SQL NVARCHAR(MAX);\n    SET @SQL = 'SELECT * FROM Orders WHERE 1=1';\n\n    -- Apply Start Date filter\n    IF @StartDate IS NOT NULL\n    BEGIN\n        SET @SQL = @SQL + ' AND OrderDate >= @StartDate';\n    END\n\n    -- Apply End Date filter\n    IF @EndDate IS NOT NULL\n    BEGIN\n        SET @SQL = @SQL + ' AND OrderDate <= @EndDate';\n    END\n\n    -- Apply Status filter\n    IF @Status IS NOT NULL\n    BEGIN\n        SET @SQL = @SQL + ' AND Status = @Status';\n    END\n\n    -- Apply Generated By User filter\n    IF @GeneratedByUser IS NOT NULL\n    BEGIN\n        SET @SQL = @SQL + ' AND GeneratedByUser = @GeneratedByUser';\n    END\n\n    -- Apply Amount filter\n    IF @MinAmount IS NOT NULL\n    BEGIN\n        SET @SQL = @SQL + ' AND Amount >= @MinAmount';\n    END\n\n    IF @MaxAmount IS NOT NULL\n    BEGIN\n        SET @SQL = @SQL + ' AND Amount <= @MaxAmount';\n    END\n\n    -- Execute the dynamic SQL\n    EXEC sp_executesql @SQL,\n        N'@StartDate DATE, @EndDate DATE, @Status VARCHAR(50), @GeneratedByUser VARCHAR(50), @MinAmount DECIMAL(18,2), @MaxAmount DECIMAL(18,2)',\n        @StartDate, @EndDate, @Status, @GeneratedByUser, @MinAmount, @MaxAmount;\nEND