CREATE TYPE tr_rad AS OBJECT (\n     name     VARCHAR2(64),\n     code     NUMBER,\n     vendor   NUMBER,\n     val      VARCHAR2(255),\n     num      NUMBER\n   );\n\n   CREATE TYPE tt_rad AS TABLE OF tr_rad;\n\n   CREATE OR REPLACE FUNCTION get_rad RETURN tt_rad PIPELINED IS\n   BEGIN\n     PIPE ROW(tr_rad('name1', 1, 1, 'val1', 1));\n     PIPE ROW(tr_rad('name2', 2, 2, 'val2', 2));\n     -- Add more rows as needed\n     RETURN;\n   END;