-- Table A\nCREATE TABLE TableA (\n    a_id NUMBER PRIMARY KEY,\n    a_name VARCHAR2(50)\n);\n\n-- Table B\nCREATE TABLE TableB (\n    b_id NUMBER PRIMARY KEY,\n    b_name VARCHAR2(50)\n);\n\n-- Junction Table\nCREATE TABLE A_B_Relation (\n    a_id NUMBER,\n    b_id NUMBER,\n    CONSTRAINT fk_a FOREIGN KEY (a_id) REFERENCES TableA(a_id),\n    CONSTRAINT fk_b FOREIGN KEY (b_id) REFERENCES TableB(b_id),\n    PRIMARY KEY (a_id, b_id)\n);