WITH RankedChildEntries AS (\n    SELECT \n        dbo_ChildEntry.ParentID,\n        dbo_ChildEntry.CountPropertys,\n        dbo_ChildEntry.DateTimeStamp,\n        ROW_NUMBER() OVER (PARTITION BY dbo_ChildEntry.ParentID ORDER BY dbo_ChildEntry.DateTimeStamp DESC) AS rn\n    FROM \n        dbo_ChildEntry\n)\nSELECT \n    dbo_Parents.ParentName,\n    RankedChildEntries.CountPropertys,\n    RankedChildEntries.DateTimeStamp\nFROM \n    dbo_Parents\nINNER JOIN \n    RankedChildEntries \nON \n    dbo_Parents.ParentID = RankedChildEntries.ParentID\nWHERE \n    RankedChildEntries.rn = 1;