CREATE TRIGGER [YourTrigger]\nON [YourTable]\nAFTER INSERT, UPDATE\nAS\nBEGIN\n    DECLARE @messageBody NVARCHAR(MAX);\n    -- Construct your message body as needed\n    SET @messageBody = 'Your command line arguments or other data';\n\n    -- Send message to Service Broker queue\n    BEGIN CONVERSATION @dialogHandle\n        FROM SERVICE [YourService]\n        TO SERVICE 'YourService'\n        ON CONTRACT [YourContract];\n\n    SEND ON CONVERSATION @dialogHandle\n        MESSAGE TYPE [YourMessageType]\n        (@messageBody);\n\n    END CONVERSATION @dialogHandle;\nEND;