DECLARE\n    child_map TMap := TMap();\n    parent_map TMap := TMap();\nBEGIN\n    -- Populate the child map\n    child_map.EXTEND;\n    child_map(child_map.LAST) := TKey('x', 'What\'s', NULL);\n    \n    child_map.EXTEND;\n    child_map(child_map.LAST) := TKey('y', 'up', NULL);\n    \n    -- Populate the parent map\n    parent_map.EXTEND;\n    parent_map(parent_map.LAST) := TKey('a', 'Hello', NULL);\n    \n    parent_map.EXTEND;\n    parent_map(parent_map.LAST) := TKey('b', 'World', NULL);\n    \n    parent_map.EXTEND;\n    parent_map(parent_map.LAST) := TKey('c', NULL, child_map);\n    \n    -- You can now use the parent_map as needed\n    DBMS_OUTPUT.PUT_LINE('Map created successfully');\nEND;\n/