CREATE FUNCTION dbo.GetNextDate (@Month TINYINT, @Day TINYINT)\nRETURNS DATETIME\nAS\nBEGIN\n    DECLARE @CurrentYear INT = YEAR(GETDATE())\n    DECLARE @CurrentDate DATE = GETDATE()\n    DECLARE @NextDate DATE = DATEFROMPARTS(@CurrentYear, @Month, @Day)\n    \n    IF @NextDate < @CurrentDate\n    BEGIN\n        SET @NextDate = DATEFROMPARTS(@CurrentYear + 1, @Month, @Day)\n    END\n\n    RETURN @NextDate\nEND