WITH RankedAdjusters AS (\n    SELECT \n        AdjusterID, \n        CompanyID, \n        FirstName, \n        LastName, \n        EmailID,\n        ROW_NUMBER() OVER (PARTITION BY CompanyID ORDER BY AdjusterID) AS rn\n    FROM \n        Adjusters\n)\nSELECT \n    AdjusterID, \n    CompanyID, \n    FirstName, \n    LastName, \n    EmailID\nFROM \n    RankedAdjusters\nWHERE \n    rn = 1;