SELECT a.constraint_name, a.table_name, a.column_name,\n       b.owner AS referenced_owner, b.table_name AS referenced_table, b.column_name AS referenced_column\nFROM all_cons_columns a\nJOIN all_constraints c ON a.constraint_name = c.constraint_name\nJOIN all_cons_columns b ON c.r_constraint_name = b.constraint_name\nWHERE c.constraint_type = 'R'\nAND b.table_name = 'YOUR_TABLE_NAME';