WITH Months AS (\n    SELECT 1 AS MonthNum UNION ALL\n    SELECT 2 UNION ALL\n    SELECT 3 UNION ALL\n    SELECT 4 UNION ALL\n    SELECT 5 UNION ALL\n    SELECT 6 UNION ALL\n    SELECT 7 UNION ALL\n    SELECT 8 UNION ALL\n    SELECT 9 UNION ALL\n    SELECT 10 UNION ALL\n    SELECT 11 UNION ALL\n    SELECT 12\n)\nSELECT \n    i.itemName,\n    m.MonthNum,\n    ISNULL(SUM(v.value), 0) AS value\nFROM \n    t_item i\nCROSS JOIN \n    Months m\nLEFT JOIN \n    t_value v ON i.itemID = v.itemID AND MONTH(v.date) = m.MonthNum\nGROUP BY \n    i.itemName, \n    m.MonthNum\nORDER BY \n    i.itemName,\n    m.MonthNum;