ALTER TABLE ForeignKeyTable DROP CONSTRAINT FK_ForeignKeyTable_YourTable;\n    ALTER TABLE YourTable DROP CONSTRAINT PK_YourTable;\n\n    ALTER TABLE YourTable DROP COLUMN OldGUID;\n    EXEC sp_rename 'YourTable.NewSequentialGUID', 'OldGUID', 'COLUMN';\n\n    ALTER TABLE ForeignKeyTable DROP COLUMN OldGUID;\n    EXEC sp_rename 'ForeignKeyTable.NewSequentialGUID', 'OldGUID', 'COLUMN';\n\n    ALTER TABLE YourTable ADD CONSTRAINT PK_YourTable PRIMARY KEY CLUSTERED (OldGUID);\n    ALTER TABLE ForeignKeyTable ADD CONSTRAINT FK_ForeignKeyTable_YourTable FOREIGN KEY (OldGUID) REFERENCES YourTable(OldGUID);