CREATE OR REPLACE PROCEDURE Serialize_Row_To_XML (\n    p_table_name IN VARCHAR2,\n    p_row_id IN NUMBER,\n    p_xml_out OUT CLOB\n) IS\n    l_sql VARCHAR2(32767);\n    l_clob CLOB;\nBEGIN\n    -- Construct dynamic SQL to generate XML from the specified table and row_id\n    l_sql := 'SELECT dbms_xmlgen.getxml(''SELECT * FROM ' || p_table_name || ' WHERE rowid = :1'') FROM dual';\n\n    -- Execute the dynamic SQL and capture the XML output in a CLOB\n    EXECUTE IMMEDIATE l_sql INTO l_clob USING p_row_id;\n\n    -- Assign the result to the output parameter\n    p_xml_out := l_clob;\nEXCEPTION\n    WHEN OTHERS THEN\n        -- Handle exceptions\n        p_xml_out := NULL;\n        RAISE_APPLICATION_ERROR(-20001, 'Error serializing row to XML: ' || SQLERRM);\nEND Serialize_Row_To_XML;\n/