SELECT \n    news.newsTitle AS title, \n    news.newsBody AS body, \n    news.postedOn AS postedOn, \n    NULL AS reviewSummary, \n    NULL AS ourScore, \n    NULL AS gameName, \n    NULL AS gameImage \nFROM \n    news \n\nUNION ALL \n\nSELECT \n    NULL AS title, \n    NULL AS body, \n    review.postedOn AS postedOn, \n    review.reviewSummary AS reviewSummary, \n    review.ourScore AS ourScore, \n    game.gameName AS gameName, \n    game.gameImage AS gameImage \nFROM \n    review \nJOIN \n    game ON review.gameID = game.gameID \n\nORDER BY \n    postedOn DESC \nLIMIT 5;