CREATE TRIGGER trgInsertYourTable\n   ON YourTable\n   AFTER INSERT\n   AS\n   BEGIN\n       UPDATE YourTable\n       SET RowHash = dbo.GenerateRowHash(Column1, Column2, ...)\n       FROM YourTable\n       INNER JOIN inserted i ON YourTable.PrimaryKey = i.PrimaryKey;\n   END;\n   GO\n\n   CREATE TRIGGER trgUpdateYourTable\n   ON YourTable\n   AFTER UPDATE\n   AS\n   BEGIN\n       UPDATE YourTable\n       SET RowHash = dbo.GenerateRowHash(Column1, Column2, ...)\n       FROM YourTable\n       INNER JOIN inserted i ON YourTable.PrimaryKey = i.PrimaryKey;\n   END;