SELECT \n    a.table_name AS referencing_table,\n    a.constraint_name AS referencing_constraint,\n    b.table_name AS referenced_table,\n    b.constraint_name AS referenced_constraint\nFROM \n    all_constraints a\nJOIN \n    all_constraints b \nON \n    a.r_constraint_name = b.constraint_name\nJOIN \n    all_cons_columns c \nON \n    a.constraint_name = c.constraint_name\nWHERE \n    b.table_name = 'YOUR_TABLE_NAME' \n    AND a.constraint_type = 'R';