SELECT\n    CASE\n        WHEN CHARINDEX('(', ColumnX, CHARINDEX('(', ColumnX) + 1) > 0 THEN \n            SUBSTRING(ColumnX, CHARINDEX('(', ColumnX, CHARINDEX('(', ColumnX) + 1) + 1, CHARINDEX(')', ColumnX, CHARINDEX('(', ColumnX, CHARINDEX('(', ColumnX) + 1)) - CHARINDEX('(', ColumnX, CHARINDEX('(', ColumnX) + 1) - 1)\n        ELSE \n            SUBSTRING(ColumnX, CHARINDEX('(', ColumnX) + 1, CHARINDEX(')', ColumnX) - CHARINDEX('(', ColumnX) - 1)\n    END AS Column1,\n    CASE\n        WHEN CHARINDEX('(', ColumnX, CHARINDEX('(', ColumnX) + 1) > 0 THEN \n            SUBSTRING(ColumnX, CHARINDEX('(', ColumnX) + 1, CHARINDEX(')', ColumnX) - CHARINDEX('(', ColumnX) - 1)\n        ELSE \n            NULL\n    END AS Column2\nFROM \n    YourTableName;