CREATE EVENT SESSION [PermissionDeniedEvents]\nON SERVER\nADD EVENT sqlserver.sql_statement_completed (\n    ACTION (sqlserver.client_app_name, sqlserver.client_hostname, sqlserver.username)\n    WHERE (sqlserver.error_number = 229) -- 229 is the error number for permission denied\n)\nADD TARGET package0.event_file (SET filename = N'PermissionDeniedEvents.xel')\nWITH (MAX_MEMORY=4096 KB, EVENT_RETENTION_MODE=ALLOW_SINGLE_EVENT_LOSS, MAX_DISPATCH_LATENCY=30 SECONDS, MAX_EVENT_SIZE=0 KB, MEMORY_PARTITION_MODE=NONE, TRACK_CAUSALITY=ON, STARTUP_STATE=OFF);\nGO\n\nALTER EVENT SESSION [PermissionDeniedEvents] ON SERVER STATE = START;\nGO