SELECT id, status, date_this_status\nFROM (\n    SELECT id, status, date_this_status\n    FROM table1\n    UNION ALL\n    SELECT id, status, date_this_status\n    FROM table1_hist\n) combined\nWHERE date_this_status = (\n    SELECT MIN(date_this_status)\n    FROM (\n        SELECT date_this_status\n        FROM table1 WHERE table1.id = combined.id\n        UNION ALL\n        SELECT date_this_status\n        FROM table1_hist WHERE table1_hist.id = combined.id\n    ) sub\n)\nORDER BY id;