CREATE TABLE Site (\n    Reference INT PRIMARY KEY,\n    Name VARCHAR(255),\n    Charge DECIMAL(10, 2)\n);\n\nCREATE TABLE Resident (\n    Reference INT,\n    Address VARCHAR(255),\n    Username VARCHAR(255),\n    Password VARCHAR(255),\n    PRIMARY KEY (Reference)\n);\n\nCREATE TABLE Residency (\n    SiteReference INT,\n    ResidentReference INT,\n    PRIMARY KEY (SiteReference, ResidentReference),\n    FOREIGN KEY (SiteReference) REFERENCES Site(Reference),\n    FOREIGN KEY (ResidentReference) REFERENCES Resident(Reference)\n);