-- Create a temporary table to store the mapping of Precnum to ID\nCREATE TEMPORARY TABLE temp_precinct_ids AS\nSELECT voterfile_household.Precnum, voterfile_precienct.ID AS PrecID\nFROM voterfile_precienct\nINNER JOIN voterfile_household ON voterfile_precienct.PREC_ID = voterfile_household.Precnum;\n\n-- Update the household table using the temporary table\nUPDATE voterfile_household\nINNER JOIN temp_precinct_ids ON voterfile_household.Precnum = temp_precinct_ids.Precnum\nSET voterfile_household.PrecID = temp_precinct_ids.PrecID;\n\n-- Drop the temporary table as it is no longer needed\nDROP TEMPORARY TABLE temp_precinct_ids;