CREATE PROCEDURE GetDataFromDynamicView\n    @TableName NVARCHAR(128),\n    @Code NVARCHAR(500),\n    @Desc NVARCHAR(500) OUTPUT\nAS\nBEGIN\n    DECLARE @SQLString NVARCHAR(MAX);\n    SET @SQLString = N'SELECT @Desc = Description FROM ' \n                     + QUOTENAME(@TableName) \n                     + ' WHERE Code = @Code;';\n\n    EXEC sp_executesql @SQLString, \n                       N'@Code NVARCHAR(500), @Desc NVARCHAR(500) OUTPUT', \n                       @Code = @Code, \n                       @Desc = @Desc OUTPUT;\nEND