DECLARE\n     TYPE b_table_type IS TABLE OF b%ROWTYPE;\n     b_table b_table_type;\n   BEGIN\n     SELECT * BULK COLLECT INTO b_table FROM b@remotedb WHERE ROWNUM <= 1000;\n     \n     FORALL i IN INDICES OF b_table\n       INSERT INTO b_local VALUES b_table(i);\n   END;\n   /\n\n   -- Now join with the local table\n   SELECT a.col, b_local.col\n   FROM a, b_local\n   WHERE a.b_id = b_local.id;