SELECT \n    A.readings_miu_id, \n    A.Reading, \n    A.ReadDate, \n    Format(A.ReadTime, 'hh:mm:ss') AS ReadTime, \n    A.MIUwindow, \n    A.SN, \n    A.Noise, \n    A.RSSI, \n    A.ColRSSI, \n    A.MIURSSI, \n    A.Firmware, \n    A.CFGDate, \n    A.FreqCorr, \n    A.Active, \n    A.MeterType, \n    A.OriginCol, \n    A.ColID, \n    A.Ownage, \n    A.SiteID, \n    A.PremID, \n    B.prem_group1, \n    B.prem_group2, \n    A.ReadID \nINTO analyzed  \nFROM Neptune_FN_Analysis AS A \nINNER JOIN Neptune_prem AS B \nON A.PremID = B.premice_id \nWHERE A.SiteID = 36801 \nAND A.ReadDate BETWEEN #04/21/09# AND #04/27/09#  \nAND A.OriginCol = 'US 29' \nAND A.ColID = 1 \nAND A.ColID <> 0 \nAND A.Active = 'Y'\nAND A.ReadTime = (\n    SELECT TOP 1 C.ReadTime \n    FROM Neptune_FN_Analysis AS C \n    WHERE C.readings_miu_id = A.readings_miu_id \n    AND C.ReadDate = A.ReadDate \n    ORDER BY C.ReadTime DESC\n);