SELECT \n  COALESCE((SELECT MAX(modifydate) FROM scormtrackings WHERE bundleid=@bundleid AND userid=u.userid), '') AS LastAccessed,\n  COALESCE((SELECT Value FROM scormtrackings WHERE bundleid=@bundleid AND userid=u.userid AND param='vegas2.progress'), '') AS CompletedLessons,\n  100 AS TotalLessons,\n  100 AS TotalNumAvail,\n  COALESCE((SELECT Value FROM scormtrackings WHERE bundleid=@bundleid AND userid=u.userid AND param='cmi.score.raw'), '') AS TotalNumCorrect\nFROM \n  your_table AS u;