-- Table for storing content items\nCREATE TABLE content (\n    id SERIAL PRIMARY KEY,\n    title VARCHAR(255),\n    content TEXT,\n    status VARCHAR(50), -- 'draft', 'published', etc.\n    created_at TIMESTAMP,\n    updated_at TIMESTAMP,\n    author_id INT\n);\n\n-- Table for storing version history\nCREATE TABLE content_versions (\n    id SERIAL PRIMARY KEY,\n    content_id INT REFERENCES content(id),\n    version INT,\n    serialized_data TEXT, -- JSON or other serialization format\n    created_at TIMESTAMP,\n    modified_at TIMESTAMP,\n    draft_date TIMESTAMP, -- For future drafts\n    author_id INT\n);