CREATE PROCEDURE UpdatePlayValue\nAS\nBEGIN\n    DECLARE @Today DATE = GETDATE();\n    DECLARE @DailyFactor INT = DATEDIFF(DAY, '1900-01-01', @Today);\n\n    -- Update daily factor table\n    UPDATE tb_daily_factor\n    SET daily_factor = @DailyFactor\n    WHERE id = 1;\n\n    -- Update playvalue for all players\n    UPDATE tb_player\n    SET playvalue = (DATEDIFF(DAY, Date_of_Birth, @Today) * Times_Played * Versions);\nEND;