-- Update the oildatasetstatus table\nUPDATE oildatasetstatus\nSET oildatasetstatusid = CASE \n    WHEN oildatasetstatusid = 5 THEN 16\n    WHEN oildatasetstatusid = 6 THEN 17\n    WHEN oildatasetstatusid = 7 THEN 18\n    WHEN oildatasetstatusid = 8 THEN 18\n    WHEN oildatasetstatusid = 9 THEN 18\n    WHEN oildatasetstatusid = 10 THEN 19\n    WHEN oildatasetstatusid = 11 THEN 20\nEND\nWHERE oildatasetid IN (\n    SELECT oildataset.oildatasetid\n    FROM oildataset\n    INNER JOIN samplepoint ON oildataset.samplepointid = samplepoint.samplepointid\n    INNER JOIN customersite ON samplepoint.customersiteid = customersite.customersiteid\n    WHERE customersite.customerid = 2\n);\n\n-- Update the oildataset table\nUPDATE oildataset\nSET oillabid = 9\nWHERE oildatasetid IN (\n    SELECT oildatasetid\n    FROM oildatasetstatus\n    WHERE oildatasetstatusid BETWEEN 16 AND 20\n);