DELIMITER //\n\nCREATE PROCEDURE your_stored_procedure(IN listOfIds VARCHAR(255))\nBEGIN\n    -- Prepare the final query using FIND_IN_SET to simulate IN clause\n    SET @query = CONCAT('SELECT * FROM your_table WHERE FIND_IN_SET(your_column, ?)');\n\n    -- Execute the prepared query\n    PREPARE stmt FROM @query;\n    EXECUTE stmt USING @listOfIds;\n    DEALLOCATE PREPARE stmt;\nEND //\n\nDELIMITER ;