CREATE PROCEDURE SelectFromDynamicTable\n    @itemNumber NVARCHAR(50),\n    @itemType CHAR(1)\nAS\nBEGIN\n    DECLARE @sql NVARCHAR(MAX)\n\n    -- Build the SQL query string based on the itemType\n    SET @sql = 'SELECT ItemNumber, ItemType, Description FROM ' +\n               CASE @itemType\n                   WHEN 'A' THEN 'TableA'\n                   ELSE 'TableB'\n               END +\n               ' WHERE ' +\n               CASE @itemType\n                   WHEN 'A' THEN 'ItemNumber = @itemNumber'\n                   ELSE 'PartNumber = @itemNumber'\n               END\n\n    -- Execute the dynamically built SQL query\n    EXEC sp_executesql @sql, N'@itemNumber NVARCHAR(50)', @itemNumber\nEND