WITH MarketerData AS (\n    SELECT \n        Marketer, \n        SubMarketer, \n        Grade, \n        Total, \n        CONVERT(varchar, Date, 101) AS [Date]\n    FROM SomeTable\n    WHERE Date >= '2/25/2009' AND Date < '2/26/2009'\n        AND Marketer IN ('22', '500')\n)\nSELECT \n    Marketer, \n    SubMarketer, \n    Grade, \n    Total, \n    [Date]\nFROM MarketerData\n\nUNION ALL\n\nSELECT \n    Marketer, \n    'Marketer Total' AS SubMarketer, \n    NULL AS Grade, \n    SUM(Total) AS Total, \n    NULL AS [Date]\nFROM MarketerData\nGROUP BY Marketer\n\nORDER BY \n    Marketer,\n    CASE WHEN SubMarketer = 'Marketer Total' THEN 1 ELSE 0 END,\n    SubMarketer,\n    Grade,\n    [Date];