SELECT \n    C.COLUMN_NAME, \n    C.DATA_TYPE, \n    COALESCE(KCU1.CONSTRAINT_NAME, 'No Constraint') AS ConstraintName, \n    COALESCE(KCU2.TABLE_NAME, 'N/A') AS ForeignTableName, \n    COALESCE(KCU2.COLUMN_NAME, 'N/A') AS ForeignColumnName\nFROM \n    INFORMATION_SCHEMA.COLUMNS AS C\nLEFT JOIN \n    INFORMATION_SCHEMA.KEY_COLUMN_USAGE AS KCU1 \n    ON C.TABLE_NAME = KCU1.TABLE_NAME \n    AND C.COLUMN_NAME = KCU1.COLUMN_NAME\nLEFT JOIN \n    INFORMATION_SCHEMA.REFERENTIAL_CONSTRAINTS AS RC \n    ON KCU1.CONSTRAINT_CATALOG = RC.CONSTRAINT_CATALOG \n    AND KCU1.CONSTRAINT_SCHEMA = RC.CONSTRAINT_SCHEMA \n    AND KCU1.CONSTRAINT_NAME = RC.CONSTRAINT_NAME\nLEFT JOIN \n    INFORMATION_SCHEMA.KEY_COLUMN_USAGE AS KCU2 \n    ON RC.UNIQUE_CONSTRAINT_CATALOG = KCU2.CONSTRAINT_CATALOG \n    AND RC.UNIQUE_CONSTRAINT_SCHEMA = KCU2.CONSTRAINT_SCHEMA \n    AND RC.UNIQUE_CONSTRAINT_NAME = KCU2.CONSTRAINT_NAME \n    AND KCU1.ORDINAL_POSITION = KCU2.ORDINAL_POSITION\nWHERE \n    C.TABLE_NAME = 'Customers';