DECLARE @UserInput NVARCHAR(255) = 'brown cow';\nDECLARE @UserInputWords TABLE (Word NVARCHAR(255));\n\n-- Split user input into words\nINSERT INTO @UserInputWords (Word)\nSELECT value FROM STRING_SPLIT(@UserInput, ' ');\n\n-- Select records that match the most words from user input\nSELECT TOP 1 \n    YourTable.YourColumn,\n    COUNT(*) AS MatchCount\nFROM \n    YourTable\nCROSS APPLY \n    (SELECT Word FROM @UserInputWords) AS UserWords\nWHERE \n    CHARINDEX(UserWords.Word, YourTable.YourColumn) > 0\nGROUP BY \n    YourTable.YourColumn\nORDER BY \n    MatchCount DESC;