WITH genealogy AS (\n  -- Start with the specified animal and its parents\n  SELECT animal_id, parent_male_id, parent_female_id\n  FROM animal\n  WHERE animal_id = 123\n  UNION ALL\n  -- Fetch the descendants of the specified animal\n  SELECT a.animal_id, a.parent_male_id, a.parent_female_id\n  FROM animal a\n  JOIN genealogy g ON a.parent_male_id = g.animal_id\n  UNION ALL\n  SELECT a.animal_id, a.parent_male_id, a.parent_female_id\n  FROM animal a\n  JOIN genealogy g ON a.parent_female_id = g.animal_id\n)\nSELECT *\nFROM genealogy;