-- Table to store survey metadata\nCREATE TABLE Surveys (\n    SurveyID INT PRIMARY KEY AUTO_INCREMENT,\n    Title VARCHAR(255),\n    CreatedDate DATETIME DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Table to store questions for each survey\nCREATE TABLE Questions (\n    QuestionID INT PRIMARY KEY AUTO_INCREMENT,\n    SurveyID INT,\n    QuestionText VARCHAR(255),\n    FOREIGN KEY (SurveyID) REFERENCES Surveys(SurveyID)\n);\n\n-- Table to store responses from each user\nCREATE TABLE Responses (\n    ResponseID INT PRIMARY KEY AUTO_INCREMENT,\n    SurveyID INT,\n    UserID INT,\n    ResponseDate DATETIME DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (SurveyID) REFERENCES Surveys(SurveyID)\n);\n\n-- Table to store individual answers to each question\nCREATE TABLE Answers (\n    AnswerID INT PRIMARY KEY AUTO_INCREMENT,\n    ResponseID INT,\n    QuestionID INT,\n    AnswerText VARCHAR(255),\n    FOREIGN KEY (ResponseID) REFERENCES Responses(ResponseID),\n    FOREIGN KEY (QuestionID) REFERENCES Questions(QuestionID)\n);