-- On Server A\nEXEC sp_trace_create @TraceID OUTPUT, 0, N'C:\TraceServerA.trc', 5, NULL;\n\n-- Add events to trace\nEXEC sp_trace_setevent @TraceID, 10, 1, 1; -- RPC:Completed\nEXEC sp_trace_setevent @TraceID, 10, 12, 1; -- SPID\nEXEC sp_trace_setevent @TraceID, 12, 1, 1; -- SQL:BatchCompleted\nEXEC sp_trace_setevent @TraceID, 12, 12, 1; -- SPID\n\n-- Set a filter to capture specific stored procedure\nEXEC sp_trace_setfilter @TraceID, 1, 0, 6, N'your_stored_proc_name';\n\n-- Start the trace\nEXEC sp_trace_setstatus @TraceID, 1;