-- Table to store different group types\nCREATE TABLE group_types (\n    id INT PRIMARY KEY AUTO_INCREMENT,\n    name VARCHAR(50) NOT NULL\n);\n\n-- Insert basic types into group_types table\nINSERT INTO group_types (name) VALUES ('buyer'), ('seller'), ('referee');\n\n-- Table to store groups\nCREATE TABLE groups (\n    id INT PRIMARY KEY AUTO_INCREMENT,\n    name VARCHAR(100) NOT NULL,\n    group_type_id INT,\n    FOREIGN KEY (group_type_id) REFERENCES group_types(id)\n);\n\n-- Table to store specialized types for buyers\nCREATE TABLE specialized_types (\n    id INT PRIMARY KEY AUTO_INCREMENT,\n    name VARCHAR(50) NOT NULL\n);\n\n-- Insert specialized types into specialized_types table\nINSERT INTO specialized_types (name) VALUES ('electrical'), ('mechanical');\n\n-- Table to associate buyers with specialized types\nCREATE TABLE buyer_specialized_types (\n    group_id INT,\n    specialized_type_id INT,\n    PRIMARY KEY (group_id, specialized_type_id),\n    FOREIGN KEY (group_id) REFERENCES groups(id),\n    FOREIGN KEY (specialized_type_id) REFERENCES specialized_types(id)\n);