WITH hier(num, lvl) AS (\n    SELECT  0, 1\n    UNION ALL\n    SELECT  num + 1, lvl + 1\n    FROM    hier\n    WHERE   lvl < 104  -- 52 weeks/year * 2 years\n)\nSELECT \n    DATEADD(day, (num * 7), DATEADD(day, -DATEPART(weekday, '2007-05-29') + 1, '2007-05-29')) AS StartDate,\n    DATEADD(day, (num * 7) + 6, DATEADD(day, -DATEPART(weekday, '2007-05-29') + 1, '2007-05-29')) AS EndDate\nFROM hier\nORDER BY num\nOPTION (MAXRECURSION 104);