-- Example of inserting and querying null values in SQL\n\n-- Creating a table with nullable columns\nCREATE TABLE employees (\n    id INT PRIMARY KEY,\n    name VARCHAR(50) NOT NULL,\n    age INT,\n    email VARCHAR(100)\n);\n\n-- Inserting data, with some null values\nINSERT INTO employees (id, name, age, email) VALUES (1, 'Alice', 30, 'alice@example.com');\nINSERT INTO employees (id, name, age, email) VALUES (2, 'Bob', NULL, NULL);\n\n-- Querying for rows with null values\nSELECT * FROM employees WHERE age IS NULL;