CREATE FUNCTION [dbo].[usf_GIS_GET_LAT]\n   (\n       @City VARCHAR(30),\n       @State CHAR(2)\n   )\n   RETURNS TABLE\n   AS\n   RETURN\n   (\n       SELECT TOP 1 LAT\n       FROM GIS_Location WITH(NOLOCK)\n       WHERE [State] = @State AND [City] = @City\n   );\n   \n   CREATE FUNCTION [dbo].[usf_GIS_GET_LON]\n   (\n       @City VARCHAR(30),\n       @State CHAR(2)\n   )\n   RETURNS TABLE\n   AS\n   RETURN\n   (\n       SELECT TOP 1 LON\n       FROM GIS_Location WITH(NOLOCK)\n       WHERE [State] = @State AND [City] = @City\n   );