-- Assuming your table is named 'your_table' and you have columns 'col1', 'col2', and 'last_modified'\n\n-- Step 1: Fetch current values\nSELECT col1, col2\nFROM your_table\nWHERE id = your_row_id;\n\n-- Step 2: Update only if values are different\nUPDATE your_table\nSET \n    col1 = new_value1, \n    col2 = new_value2, \n    last_modified = CURRENT_TIMESTAMP\nWHERE \n    id = your_row_id\n    AND (\n        col1 <> new_value1 OR\n        col2 <> new_value2\n    );