CREATE OR REPLACE PROCEDURE populate_temp_table(p_csv_string VARCHAR2) \nAS\n  v_split_data SYS.ODCIVARCHAR2LIST;\nBEGIN\n  -- Split the CSV string\n  v_split_data := split_csv(p_csv_string);\n  \n  -- Insert the split values into the temp table\n  FOR i IN 1 .. v_split_data.COUNT LOOP\n    INSERT INTO temp_csv_data (id) VALUES (v_split_data(i));\n  END LOOP;\nEND populate_temp_table;\n/