CREATE TABLE Products (\n    ProductGUID UNIQUEIDENTIFIER NOT NULL DEFAULT NEWID(), -- or NEWSEQUENTIALID() in SQL Server\n    ProductID INT IDENTITY(1,1) NOT NULL,\n    ProductName NVARCHAR(255) NOT NULL,\n    PRIMARY KEY (ProductGUID),\n    UNIQUE (ProductID) -- Ensure ProductID remains unique\n);\n\n-- Example of inserting a new product\nINSERT INTO Products (ProductName) VALUES ('Example Product');\n\n-- Example of selecting a product by user-friendly ID\nSELECT * FROM Products WHERE ProductID = 1;\n\n-- Example of foreign key referencing the GUID\nCREATE TABLE Orders (\n    OrderID INT IDENTITY(1,1) NOT NULL,\n    ProductGUID UNIQUEIDENTIFIER NOT NULL,\n    Quantity INT NOT NULL,\n    PRIMARY KEY (OrderID),\n    FOREIGN KEY (ProductGUID) REFERENCES Products(ProductGUID)\n);