CREATE TRIGGER YourTriggerName\nON YourTableName\nAFTER INSERT, UPDATE, DELETE\nAS\nBEGIN\n    DECLARE @TableName NVARCHAR(128)\n    DECLARE @SchemaName NVARCHAR(128)\n    DECLARE @TriggerName NVARCHAR(128)\n\n    -- Get the trigger name\n    SET @TriggerName = OBJECT_NAME(@@PROCID)\n\n    -- Get the table name and schema name\n    SELECT \n        @TableName = OBJECT_NAME(parent_id), \n        @SchemaName = SCHEMA_NAME(schema_id)\n    FROM \n        sys.objects\n    WHERE \n        object_id = @@PROCID\n\n    -- Now you can use @TableName and @SchemaName as needed\n    PRINT 'Table Name: ' + @TableName\n    PRINT 'Schema Name: ' + @SchemaName\nEND