CREATE TABLE Employee (\n        EmployeeID INT PRIMARY KEY,\n        PersonID INT,\n        HireDate DATE,\n        Department VARCHAR(100),\n        FOREIGN KEY (PersonID) REFERENCES Person(PersonID)\n    );\n\n    CREATE TABLE Student (\n        StudentID INT PRIMARY KEY,\n        PersonID INT,\n        EnrollmentDate DATE,\n        Major VARCHAR(100),\n        FOREIGN KEY (PersonID) REFERENCES Person(PersonID)\n    );