-- Create a combined search table\nCREATE TABLE combined_search (\n    id SERIAL PRIMARY KEY,\n    entity_type TEXT,\n    entity_id INT,\n    search_text TSVECTOR\n);\n\n-- Populate the combined search table with data from tenants\nINSERT INTO combined_search (entity_type, entity_id, search_text)\nSELECT 'tenant', id, to_tsvector(name || ' ' || email || ' ' || phone)\nFROM tenants;\n\n-- Populate the combined search table with data from landlords\nINSERT INTO combined_search (entity_type, entity_id, search_text)\nSELECT 'landlord', id, to_tsvector(name || ' ' || email || ' ' || phone)\nFROM landlords;\n\n-- Repeat for other tables...\n\n-- Create an index on the search_text column\nCREATE INDEX idx_combined_search_text ON combined_search USING GIN(search_text);\n\n-- Perform a search query\nSELECT * FROM combined_search\nWHERE search_text @@ to_tsquery('search_term');